    describe("POST /reset-password/:token", () => {
      it("should reset the password with a valid token", done => {
        _chai.default.request(_server.default).post(`/reset-password/${resetToken}`).send({
          newPassword
        }).end((err, res) => {
          expect(res).to.have.status(200);
          expect(res.body).to.have.property("message").eql("Password has been reset successfully");
          done();
        });
      });
      it("should return an error with an invalid token", done => {
        _chai.default.request(_server.default).post(`/reset-password/invalid_token`).send({
          newPassword
        }).end((err, res) => {
          expect(res).to.have.status(400);
          expect(res.body).to.have.property("error").eql("Invalid or expired token");
          done();
        });
      });
    });

