    describe("POST /login with new password", () => {
      it("should allow login with the new password", done => {
        _chai.default.request(_server.default).get("/connect").set("Authorization", "Basic " + btoa("sandy@example.com" + ":" + "newPassword123")).end((err, res) => {
          expect(res).to.have.status(200);
          expect(res.body).to.have.property("token");
